import React, { useState } from "react";
import axios from "axios";

const TransactionForm = ({ userId, onTransactionComplete }) => {
  const [symbol, setSymbol] = useState("");
  const [quantity, setQuantity] = useState("");
  const [price, setPrice] = useState("");
  const [transactionType, setTransactionType] = useState("buy");

  const handleSubmit = (e) => {
    e.preventDefault();

    const quantityNum = parseFloat(quantity);
    const priceNum = parseFloat(price);

    if (!symbol || quantityNum <= 0 || priceNum <= 0) {
      alert("Please enter valid symbol, quantity, and price.");
      return;
    }

    const transaction = {
      userId,
      symbol,
      quantity: quantityNum,
      price: priceNum,
      type: transactionType,
      total: quantityNum * priceNum
    };

    axios.post("http://localhost:8080/api/transactions", transaction)
      .then(() => {
        alert("Transaction successful!");
        setSymbol("");
        setQuantity("");
        setPrice("");
        if (onTransactionComplete) {
          onTransactionComplete(); // извикваме функцията от родителя
        }
      })
      .catch(error => alert("Error: " + error.message));
  };

  return (
    <div>
      <h2>New Transaction</h2>
      <form onSubmit={handleSubmit}>
        <input
          type="text"
          placeholder="Symbol (e.g., BTC)"
          value={symbol}
          onChange={(e) => setSymbol(e.target.value.toUpperCase())}
          required
        />
        <input
          type="number"
          placeholder="Quantity"
          value={quantity}
          onChange={(e) => setQuantity(e.target.value)}
          min="0.000001"
          step="0.000001"
          required
        />
        <input
          type="number"
          placeholder="Price (USD)"
          value={price}
          onChange={(e) => setPrice(e.target.value)}
          min="0.01"
          step="0.01"
          required
        />
        <select value={transactionType} onChange={(e) => setTransactionType(e.target.value)}>
          <option value="buy">Buy</option>
          <option value="sell">Sell</option>
        </select>
        <button type="submit">Submit</button>
      </form>
    </div>
  );
};

export default TransactionForm;
