import React, { useState } from "react";
import Balance from "./Balance";
import HoldingsTable from "./HoldingsTable";
import TransactionForm from "./TransactionForm";
import CryptoPrices from "./CryptoPrices"; // нов ред
import TransactionHistory from "./TransactionHistory"; 

const Dashboard = ({ userId }) => {
  const [refreshCounter, setRefreshCounter] = useState(0);

  const refreshData = () => {
    setRefreshCounter(prev => prev + 1);
  };

  return (
    <div>
      <Balance userId={userId} refreshKey={refreshCounter} />
      import axios from "axios";

// вътре в return:
<button onClick={() => {
  axios.put(`http://localhost:8080/api/users/${userId}/reset`)
    .then(() => {
      alert("Балансът е ресетнат до 10 000 USD.");
      refreshData(); // презареждане на данните
    })
    .catch(err => {
      alert("Грешка при ресетване на баланса");
      console.error(err);
    });
}}>
  Ресетни Баланса
</button>

      <HoldingsTable userId={userId} refreshKey={refreshCounter} />
      <TransactionForm userId={userId} onTransactionComplete={refreshData} />
      <CryptoPrices /> {/* нов ред */}
      <TransactionHistory userId={userId} />

    </div>
  );
};

export default Dashboard;
